@prefix ns1: <http://www.w3.org/ns/oa#> .
@prefix ns2: <http://www.iana.org/assignments/> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix rec: <https://www.opengis.net/def/ogc-api/records/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a <http://example.com/records/image/png> ;
                    rdfs:label "World Ozone and Ultraviolet Radiation Data Centre (WOUDC) stations" ;
                    ns2:relation <http://www.iana.org/assignments/relation/describes> ;
                    ns1:hasTarget "https://geo.woudc.org/ows?service=WMS&version=1.3.0&request=GetMap&crs={crs}&bbox={fail-to-match-bbox}&layers=stations&width={width}&height={height}&format=image/png"^^xsd:string ;
                    rec:hasVariable <http://example.com/variables/bbox>,
                        <http://example.com/variables/crs>,
                        <http://example.com/variables/height>,
                        <http://example.com/variables/width> ] ;
            sh:resultMessage "variables must exist in uri template" ;
            sh:resultPath rec:hasVariable ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraint [ sh:select """
PREFIX rec:    <https://www.opengis.net/def/ogc-api/records/>
      prefix oa: <http://www.w3.org/ns/oa#>
      SELECT $this ?path ?value ?template
      WHERE {
        BIND (rec:hasVariable as ?path)
        $this rec:hasVariable ?var .
        $this oa:hasTarget ?template .
        BIND ( STRAFTER ( STR(?var),"http://example.com/variables/") AS ?value )
      }
      GROUP BY $this ?path ?value ?template
      HAVING ( COUNT(?var) != SUM(
         IF(
            CONTAINS(?template, CONCAT("{",?value,"}"  )),1,0 )
  ) )

    """ ] ;
            sh:sourceConstraintComponent sh:SPARQLConstraintComponent ;
            sh:sourceShape <https://www.ogc.org/rules/rec/#testVarExists> ;
            sh:value "bbox" ] .

@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms true .
